import { Component, OnInit } from '@angular/core';
import { GameService } from '../../services/game.service';
import { Player } from '../../models/player.model';

@Component({
  selector: 'app-hud',
  templateUrl: './hud.component.html',
  styleUrls: ['./hud.component.css']
})
export class HudComponent implements OnInit {
  player: Player = {
    name: 'Anthony',
    level: 1,
    xp: 0,
    hearts: 3,
    maxHearts: 3,
    coins: 0,
    items: []
  };

  constructor(private gameService: GameService) {}

  ngOnInit(): void {
    this.gameService.player$.subscribe(player => {
      this.player = player;
    });
  }

  getXpProgress(): number {
    const maxLevel = 4;
    const xpPerLevel = 10;
    
    // Se j√° est√° no n√≠vel m√°ximo, mostrar 100%
    if (this.player.level >= maxLevel) {
      return 100;
    }
    
    // XP necess√°rio para o n√≠vel atual (n√≠vel 1 = 0, n√≠vel 2 = 10, n√≠vel 3 = 20, etc.)
    const currentLevelXp = (this.player.level - 1) * xpPerLevel;
    // XP necess√°rio para o pr√≥ximo n√≠vel
    const nextLevelXp = this.player.level * xpPerLevel;
    
    // XP ganho no n√≠vel atual
    const xpInLevel = this.player.xp - currentLevelXp;
    // XP necess√°rio para subir de n√≠vel
    const xpNeeded = nextLevelXp - currentLevelXp;
    
    // Calcular porcentagem (limitar entre 0 e 100)
    const progress = Math.min(100, Math.max(0, (xpInLevel / xpNeeded) * 100));
    return progress;
  }

  getHeartsArray(): boolean[] {
    return Array(this.player.maxHearts).fill(false).map((_, i) => i < this.player.hearts);
  }

  buyHeart(): void {
    const success = this.gameService.buyHeart();
    if (success) {
      // Vida comprada com sucesso
    } else {
      // N√£o tem moedas suficientes ou j√° est√° no m√°ximo
      if (this.player.coins < 10) {
        alert('Voc√™ n√£o tem moedas suficientes! Precisa de 10 moedas para comprar uma vida.');
      } else if (this.player.hearts >= this.player.maxHearts) {
        alert('Voc√™ j√° est√° com todas as vidas!');
      }
    }
  }
}

<div class="hud-container">
  <div class="hud-section">
    <div class="hud-label">‚ù§Ô∏è Vidas</div>
    <div class="hearts-container">
      <span 
        *ngFor="let hasHeart of getHeartsArray(); let i = index" 
        class="heart" 
        [class.full]="hasHeart" 
        [class.empty]="!hasHeart"
        [class.clickable]="!hasHeart && player.coins >= 10 && player.hearts < player.maxHearts"
        [title]="!hasHeart && player.coins >= 10 && player.hearts < player.maxHearts ? 'Clique para comprar vida (10 moedas)' : ''"
        (click)="!hasHeart && player.coins >= 10 && player.hearts < player.maxHearts ? buyHeart() : null">
        {{ hasHeart ? '‚ù§Ô∏è' : 'ü§ç' }}
      </span>
    </div>
  </div>

  <div class="hud-section">
    <div class="hud-label">‚≠ê N√≠vel {{ player.level }}</div>
    <div class="xp-container">
      <div class="xp-bar">
        <div class="xp-fill" [style.width.%]="getXpProgress()"></div>
      </div>
      <div class="xp-text">{{ player.xp }} XP</div>
    </div>
  </div>

  <div class="hud-section">
    <div class="hud-label">üí∞ Moedas</div>
    <div class="coins-display">{{ player.coins }} ü™ô</div>
  </div>
</div>
