import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { MissionsService } from '../../services/missions.service';
import { GameService } from '../../services/game.service';
import { MissionRewardsService } from '../../services/mission-rewards.service';
import { Mission, Activity } from '../../models/mission.model';

@Component({
  selector: 'app-mission-detail',
  templateUrl: './mission-detail.component.html',
  styleUrls: ['./mission-detail.component.css']
})
export class MissionDetailComponent implements OnInit {
  mission: Mission | undefined;
  allActivitiesCompleted = false;
  showOriginalImage = false;
  selectedMatches: { [key: string]: string } = {};
  selectedImageForMatch: { [activityIndex: number]: string | null } = {};
  shuffledMatchPairs: { [activityIndex: number]: { images: any[], texts: any[] } } = {};
  shuffledOptions: { [activityIndex: number]: any[] } = {};
  environmentAnswers: { [key: number]: boolean | null } = {};
  inputAnswers: { [key: string]: string } = {}; // Respostas dos campos de input
  cduAnswers: { [key: string]: { centena: string; dezena: string; unidade: string } } = {}; // Respostas C D U
  
  correctAnswers = 0;
  totalQuestions = 0;
  wrongAnswers = 0;
  showBonusChallenge = false;
  bonusChallengeCompleted = false;
  completedActivities: boolean[] = [];
  bonusAnswerSelected: string | null = null;
  showHeartAnimation = false;
  showFinalCard = false;
  showCoinsLossCard = false;
  coinsLost = 0;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private missionsService: MissionsService,
    public gameService: GameService,
    private rewardsService: MissionRewardsService
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const id = Number(params.get('id'));
      this.loadMission(id);
    });

    this.gameService.player$.subscribe(player => {
      if (this.showBonusChallenge && !this.bonusChallengeCompleted && player.hearts > 0) {
        this.bonusChallengeCompleted = true;
        this.showBonusChallenge = false;
        this.enableAllActivities();
      }
    });
  }

  loadMission(id: number): void {
    this.mission = this.missionsService.getMissionById(id);
    this.allActivitiesCompleted = false;
    this.showOriginalImage = !(this.mission?.textContent || this.mission?.textImages);
    this.selectedMatches = {};
    this.selectedImageForMatch = {};
    this.environmentAnswers = {};
    this.shuffledMatchPairs = {};
    this.shuffledOptions = {};
    this.inputAnswers = {};
    this.cduAnswers = {};
    
    if (this.mission) {
      this.mission.activities.forEach((activity, activityIndex) => {
        if (activity.type === 'match' && activity.matchPairs) {
          this.shuffleMatchPairs(activityIndex, activity.matchPairs);
        } else if (activity.type === 'multiple-choice' && activity.options) {
          this.shuffleOptions(activityIndex, activity.options);
        }
      });
    }
    this.correctAnswers = 0;
    this.totalQuestions = 0;
    if (this.mission) {
      this.mission.activities.forEach(activity => {
        if (activity.type === 'match' && activity.matchPairs) {
          this.totalQuestions += activity.matchPairs.length;
        } else {
          this.totalQuestions += 1;
        }
      });
    }
    this.wrongAnswers = 0;
    this.showBonusChallenge = false;
    this.bonusChallengeCompleted = false;
    this.bonusAnswerSelected = null;
    this.showHeartAnimation = false;
    this.showCoinsLossCard = false;
    this.coinsLost = 0;
    this.completedActivities = new Array(this.totalQuestions).fill(false);
    
    if (!this.mission) {
      this.router.navigate(['/missions']);
      return;
    }
    
    setTimeout(() => {
      const buttons = document.querySelectorAll('.activity-item .option-btn');
      buttons.forEach((btn: any) => {
        btn.disabled = false;
        btn.classList.remove('correct', 'incorrect', 'selected');
      });
      
      const successSection = document.getElementById('success-section');
      if (successSection) {
        successSection.style.display = 'none';
      }
    }, 100);
  }

  toggleOriginalImage(): void {
    this.showOriginalImage = !this.showOriginalImage;
  }

  getTextParagraphs(): string[] {
    if (!this.mission || !this.mission.textContent) {
      return [];
    }
    return this.mission.textContent.split('\n').filter(p => p.trim());
  }

  hasImagesInOptions(activity: Activity): boolean {
    return activity.options?.some(opt => !!opt.image) || false;
  }

  goBack(): void {
    this.router.navigate(['/missions']);
  }

  checkAnswer(activityIndex: number, optionIndex: number, isCorrect: boolean, option?: any): void {
    if (!this.mission) return;
    
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      this.checkForBonusChallenge();
      return;
    }
    
    if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
      return;
    }
    
    const activity = this.mission.activities[activityIndex];
    const options = this.shuffledOptions[activityIndex] || activity.options || [];
    const buttons = document.querySelectorAll(`.activity-item:nth-child(${activityIndex + 1}) .option-btn`) as NodeListOf<HTMLElement>;
    
    if (activity.allowMultiple) {
      const clickedBtn = buttons[optionIndex];
      const wasSelected = clickedBtn.classList.contains('selected');
      
      if (wasSelected) {
        clickedBtn.classList.remove('selected', isCorrect ? 'correct' : 'incorrect');
      } else {
        clickedBtn.classList.add('selected');
        clickedBtn.classList.add(isCorrect ? 'correct' : 'incorrect');
        
        if (!isCorrect) {
          this.wrongAnswers++;
          this.gameService.loseHeart();
          this.checkForBonusChallenge();
        }
      }
      
      const allCorrectSelected = options.every((opt: any, idx: number) => {
        if (opt.correct) {
          return buttons[idx].classList.contains('selected');
        }
        return true;
      });
      
      const noIncorrectSelected = options.every((opt: any, idx: number) => {
        if (!opt.correct) {
          return !buttons[idx].classList.contains('selected');
        }
        return true;
      });
      
      const activityCorrect = allCorrectSelected && noIncorrectSelected;
      
      if (allCorrectSelected && activityCorrect && !this.completedActivities[activityIndex]) {
        this.completedActivities[activityIndex] = true;
        this.correctAnswers++;
        buttons.forEach((btn: any) => {
          btn.disabled = true;
          options.forEach((opt: any, idx: number) => {
            if (!opt.correct && !buttons[idx].classList.contains('selected')) {
              buttons[idx].classList.add('incorrect');
            }
          });
        });
        this.checkAllActivitiesCompleted();
      } else if (!activityCorrect && this.completedActivities[activityIndex]) {
        this.completedActivities[activityIndex] = false;
        this.correctAnswers--;
      } else if (activityCorrect) {
        this.checkAllActivitiesCompleted();
      }
    } else {
      buttons.forEach((btn: any, index: number) => {
        btn.disabled = true;
        const currentOption = options[index];
        
        if (index === optionIndex) {
          btn.classList.add(isCorrect ? 'correct' : 'incorrect');
        } else if (isCorrect) {
          if (currentOption && currentOption.correct) {
            btn.classList.add('correct');
          }
        }
      });
      
      if (isCorrect && !this.completedActivities[activityIndex]) {
        this.completedActivities[activityIndex] = true;
        this.correctAnswers++;
      } else if (!isCorrect) {
        this.wrongAnswers++;
        this.gameService.loseHeart();
        this.checkForBonusChallenge();
      }
      
      this.checkAllActivitiesCompleted();
    }
  }
  
  checkForBonusChallenge(): void {
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      if (!this.showBonusChallenge) {
        this.showBonusChallenge = true;
        this.disableAllActivities();
        const successSection = document.getElementById('success-section');
        if (successSection) {
          successSection.style.display = 'none';
        }
        window.scrollTo({ top: 0, behavior: 'smooth' });
      }
    }
  }
  
  closeBonusPopup(): void {
    // Não permitir fechar o popup sem completar o desafio
    // O popup só fecha quando o desafio é completado
  }
  
  disableAllActivities(): void {
    if (!this.mission) return;
    
    this.mission.activities.forEach((activity, activityIndex) => {
      const buttons = document.querySelectorAll(`.activity-item:nth-child(${activityIndex + 1}) .option-btn`);
      buttons.forEach((btn: any) => {
        if (!btn.disabled || !btn.classList.contains('correct')) {
          btn.disabled = true;
          btn.style.opacity = '0.5';
          btn.style.cursor = 'not-allowed';
        }
      });
    });
  }
  
  enableAllActivities(): void {
    if (!this.mission) return;
    
    this.mission.activities.forEach((activity, activityIndex) => {
      if (!this.completedActivities[activityIndex]) {
        const buttons = document.querySelectorAll(`.activity-item:nth-child(${activityIndex + 1}) .option-btn`);
        buttons.forEach((btn: any) => {
          if (!btn.classList.contains('correct')) {
            btn.disabled = false;
            btn.style.opacity = '1';
            btn.style.cursor = 'pointer';
          }
        });
      }
    });
  }

  checkTrueFalse(activityIndex: number, userAnswer: boolean, isCorrect: boolean): void {
    if (!this.mission) return;
    
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      this.checkForBonusChallenge();
      return;
    }
    
    if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
      return;
    }
    
    const isActivityCorrect = isCorrect === userAnswer;
    if (isActivityCorrect && !this.completedActivities[activityIndex]) {
      this.completedActivities[activityIndex] = true;
      this.correctAnswers++;
    } else if (!isActivityCorrect) {
      this.wrongAnswers++;
      this.gameService.loseHeart();
      this.checkForBonusChallenge();
    }
    
    const buttons = document.querySelectorAll(`.activity-item:nth-child(${activityIndex + 1}) .option-btn`);
    buttons.forEach((btn: any) => {
      btn.disabled = true;
      const isTrueBtn = btn.textContent.includes('Verdadeiro');
      if ((isTrueBtn && userAnswer) || (!isTrueBtn && !userAnswer)) {
        btn.classList.add(isCorrect ? 'correct' : 'incorrect');
      } else {
        btn.classList.add(!isCorrect ? 'correct' : 'incorrect');
      }
    });
    
    this.checkAllActivitiesCompleted();
  }

  checkAllActivitiesCompleted(): void {
    if (!this.mission) return;
    
    let allCompleted = true;
    
    this.mission.activities.forEach((activity, activityIndex) => {
      if (activity.type === 'multiple-choice' || activity.type === 'true-false') {
        if (activity.allowMultiple) {
          if (!this.completedActivities[activityIndex]) {
            allCompleted = false;
          }
        } else {
          const buttons = document.querySelectorAll(`.activity-item:nth-child(${activityIndex + 1}) .option-btn`);
          const allDisabled = Array.from(buttons).every((btn: any) => btn.disabled);
          if (!allDisabled) {
            allCompleted = false;
          }
        }
      } else if (activity.type === 'match') {
        if (activity.matchPairs) {
          const allMatched = activity.matchPairs.every((pair, idx) => {
            const key = `${activityIndex}-${pair.matchId}`;
            return this.selectedMatches[key] === pair.description;
          });
          if (!allMatched) {
            allCompleted = false;
          }
        }
      } else if (activity.type === 'environment-check') {
        if (activity.environmentImages) {
          const allAnswered = activity.environmentImages.every((_, idx) => {
            return this.environmentAnswers[idx] !== null && this.environmentAnswers[idx] !== undefined;
          });
          if (!allAnswered) {
            allCompleted = false;
          }
        }
      } else if (activity.type === 'fill-blank' || activity.type === 'math-input') {
        if (!this.completedActivities[activityIndex]) {
          allCompleted = false;
        }
      } else if (activity.type === 'cdu-operation') {
        if (!this.completedActivities[activityIndex]) {
          allCompleted = false;
        }
      } else if (activity.type === 'visual-multiplication') {
        if (!this.completedActivities[activityIndex]) {
          allCompleted = false;
        }
      }
    });
    
    if (allCompleted && !this.allActivitiesCompleted) {
      this.allActivitiesCompleted = true;
      const player = this.gameService.getPlayer();
      if (player.hearts === 0 && !this.bonusChallengeCompleted) {
        this.checkForBonusChallenge();
        return;
      }
      setTimeout(() => {
        this.showSuccessMessage();
      }, 1000);
    }
  }

  showSuccessMessage(): void {
    if (this.mission) {
      if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
        return;
      }
      
      this.missionsService.markMissionAsCompleted(this.mission.id);
      
      const reward = this.rewardsService.getReward(this.mission.id);
      if (reward) {
        this.gameService.completeMission(reward, this.correctAnswers, this.totalQuestions);
      }
      
      const successSection = document.getElementById('success-section');
      if (successSection) {
        successSection.style.display = 'block';
        successSection.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }

  selectBonusAnswer(answer: string): void {
    this.bonusAnswerSelected = answer;
  }

  completeBonusChallenge(): void {
    if (!this.mission || !this.bonusAnswerSelected) return;
    
    let correctAnswer: string;
    if (this.mission.bonusQuestion) {
      correctAnswer = this.mission.bonusCorrectAnswer || "Sim";
    } else {
      correctAnswer = "Somar um número várias vezes";
    }
    
    const isCorrect = this.bonusAnswerSelected === correctAnswer;
    
    if (isCorrect) {
      this.gameService.recoverHeart();
      this.bonusChallengeCompleted = true;
      this.showBonusChallenge = false;
      this.enableAllActivities();
      this.showHeartAnimation = true;
      setTimeout(() => {
        this.showHeartAnimation = false;
        if (this.allActivitiesCompleted) {
          this.showSuccessMessage();
        } else {
          this.checkAllActivitiesCompleted();
        }
      }, 3000);
    } else {
      const player = this.gameService.getPlayer();
      const livesLost = player.maxHearts - player.hearts;
      const coinsToDeduct = livesLost * 3;
      
      if (coinsToDeduct > 0) {
        this.coinsLost = coinsToDeduct;
        this.gameService.removeCoins(coinsToDeduct);
        this.showCoinsLossCard = true;
        setTimeout(() => {
          this.showCoinsLossCard = false;
        }, 3000);
      }
      
      this.bonusAnswerSelected = null;
      this.showBonusChallenge = true;
      this.disableAllActivities();
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  }

  nextMission(): void {
    if (!this.mission) return;
    
    const nextId = this.mission.id + 1;
    const nextMission = this.missionsService.getMissionById(nextId);
    
    if (nextMission) {
      this.router.navigate(['/mission', nextId]).then(() => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      });
    } else {
      this.showFinalCard = true;
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  }

  closeFinalCard(): void {
    this.showFinalCard = false;
    this.router.navigate(['/missions']);
  }

  shuffleOptions(activityIndex: number, options: any[]): void {
    const shuffle = (array: any[]) => {
      const shuffled = [...array];
      for (let i = shuffled.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
      }
      return shuffled;
    };
    
    this.shuffledOptions[activityIndex] = shuffle(options);
  }

  shuffleMatchPairs(activityIndex: number, matchPairs: any[]): void {
    const images = [...matchPairs];
    const texts = [...matchPairs];
    
    const shuffle = (array: any[]) => {
      const shuffled = [...array];
      for (let i = shuffled.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
      }
      return shuffled;
    };
    
    this.shuffledMatchPairs[activityIndex] = {
      images: shuffle(images),
      texts: shuffle(texts)
    };
  }

  selectImageForMatch(activityIndex: number, imageId: string): void {
    if (!this.mission) return;
    
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      this.checkForBonusChallenge();
      return;
    }
    
    if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
      return;
    }
    
    if (this.selectedImageForMatch[activityIndex] === imageId) {
      this.selectedImageForMatch[activityIndex] = null;
    } else {
      this.selectedImageForMatch[activityIndex] = imageId;
    }
  }

  selectMatch(activityIndex: number, imageId: string, description: string): void {
    if (!this.mission) return;
    
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      this.checkForBonusChallenge();
      return;
    }
    
    if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
      return;
    }
    
    if (!this.selectedImageForMatch[activityIndex]) {
      return;
    }
    
    const selectedImageId = this.selectedImageForMatch[activityIndex];
    const key = `${activityIndex}-${selectedImageId}`;
    
    const activity = this.mission.activities[activityIndex];
    if (activity.matchPairs) {
      const correctPair = activity.matchPairs.find(pair => pair.matchId === selectedImageId);
      const isCorrect = correctPair && correctPair.description === description;
      
      if (isCorrect) {
        const wasAlreadyMatched = this.selectedMatches[key] !== undefined;
        this.selectedMatches[key] = description;
        
        if (!wasAlreadyMatched) {
          this.correctAnswers++;
        }
        
        this.selectedImageForMatch[activityIndex] = null;
      } else {
        this.wrongAnswers++;
        this.gameService.loseHeart();
        this.checkForBonusChallenge();
        this.selectedImageForMatch[activityIndex] = null;
      }
      
      const allCorrect = activity.matchPairs.every(pair => {
        const matchKey = `${activityIndex}-${pair.matchId}`;
        return this.selectedMatches[matchKey] === pair.description;
      });
      
      const allMatched = activity.matchPairs.every(pair => {
        const matchKey = `${activityIndex}-${pair.matchId}`;
        return this.selectedMatches[matchKey] !== undefined;
      });
      
      if (allCorrect && allMatched && !this.completedActivities[activityIndex]) {
        this.completedActivities[activityIndex] = true;
        this.checkAllActivitiesCompleted();
      } else if ((!allCorrect || !allMatched) && this.completedActivities[activityIndex]) {
        this.completedActivities[activityIndex] = false;
      }
    }
  }

  checkEnvironment(activityIndex: number, imageIndex: number, hasProblem: boolean): void {
    if (!this.mission) return;
    
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      this.checkForBonusChallenge();
      return;
    }
    
    if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
      return;
    }
    const activity = this.mission.activities[activityIndex];
    if (activity.type === 'environment-check' && activity.environmentImages) {
      this.environmentAnswers[imageIndex] = hasProblem;
      
      const correct = activity.environmentImages[imageIndex].hasProblem === hasProblem;
      
      const allAnswered = activity.environmentImages.every((img, idx) => {
        return this.environmentAnswers[idx] !== null && this.environmentAnswers[idx] !== undefined;
      });
      
      let allImagesCorrect = true;
      for (let i = 0; i < activity.environmentImages.length; i++) {
        const img = activity.environmentImages[i];
        const userAnswer = this.environmentAnswers[i];
        if (userAnswer === null || userAnswer === undefined) {
          allImagesCorrect = false;
          break;
        }
        if (userAnswer !== img.hasProblem) {
          allImagesCorrect = false;
          break;
        }
      }
      
      if (allImagesCorrect && allAnswered && !this.completedActivities[activityIndex]) {
        this.completedActivities[activityIndex] = true;
        this.correctAnswers++;
        this.checkAllActivitiesCompleted();
      } else if ((!allImagesCorrect || !allAnswered) && this.completedActivities[activityIndex]) {
        this.completedActivities[activityIndex] = false;
        this.correctAnswers--;
      } else if (!correct) {
        this.wrongAnswers++;
        this.gameService.loseHeart();
        this.checkForBonusChallenge();
      }
    }
  }

  isEnvironmentCorrect(imageIndex: number): boolean | null {
    return this.environmentAnswers[imageIndex] ?? null;
  }

  getCurrentHeartsArray(): boolean[] {
    const player = this.gameService.getPlayer();
    return Array(player.maxHearts).fill(false).map((_, i) => i < player.hearts);
  }

  // Métodos para campos de input
  getInputKey(activityIndex: number, fieldIndex: number): string {
    return `${activityIndex}-${fieldIndex}`;
  }

  getInputValue(activityIndex: number, fieldIndex: number): string {
    return this.inputAnswers[this.getInputKey(activityIndex, fieldIndex)] || '';
  }

  setInputValue(activityIndex: number, fieldIndex: number, value: string): void {
    const key = this.getInputKey(activityIndex, fieldIndex);
    this.inputAnswers[key] = value;
    
    // Verificar tipo de atividade
    if (this.mission) {
      const activity = this.mission.activities[activityIndex];
      if (activity.type === 'visual-multiplication') {
        setTimeout(() => {
          this.checkVisualMultiplication(activityIndex);
        }, 100);
      } else {
        this.checkInputActivity(activityIndex);
      }
    }
  }

  checkInputActivity(activityIndex: number): void {
    if (!this.mission) return;
    
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      this.checkForBonusChallenge();
      return;
    }
    
    if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
      return;
    }
    
    const activity = this.mission.activities[activityIndex];
    if (activity.type === 'fill-blank' || activity.type === 'math-input') {
      if (activity.inputFields) {
        // Verificar se todos os campos estão preenchidos
        let allFilled = true;
        activity.inputFields.forEach((field, fieldIndex) => {
          const key = this.getInputKey(activityIndex, fieldIndex);
          const userAnswer = this.inputAnswers[key] || '';
          if (!userAnswer.trim()) {
            allFilled = false;
          }
        });
        
        if (!allFilled) {
          return; // Ainda não preencheu tudo
        }
        
        let allCorrect = true;
        let hasError = false;
        activity.inputFields.forEach((field, fieldIndex) => {
          const key = this.getInputKey(activityIndex, fieldIndex);
          const userAnswer = this.inputAnswers[key] || '';
          const correctValue = String(field.correctValue).trim().toLowerCase();
          const userValue = userAnswer.trim().toLowerCase();
          
          if (userValue !== correctValue) {
            allCorrect = false;
            hasError = true;
          }
        });
        
        if (allCorrect && !this.completedActivities[activityIndex]) {
          this.completedActivities[activityIndex] = true;
          this.correctAnswers++;
          this.checkAllActivitiesCompleted();
        } else if (!allCorrect && this.completedActivities[activityIndex]) {
          this.completedActivities[activityIndex] = false;
          this.correctAnswers--;
        } else if (hasError && !this.completedActivities[activityIndex]) {
          // Erro ao tentar completar - perde uma vida
          this.wrongAnswers++;
          this.gameService.loseHeart();
          this.checkForBonusChallenge();
        }
      }
    } else if (activity.type === 'cdu-operation' && activity.cduOperation) {
      const op = activity.cduOperation;
      const key = `${activityIndex}-cdu`;
      const userCDU = this.cduAnswers[key];
      
      if (userCDU && userCDU.centena && userCDU.dezena && userCDU.unidade) {
        const userCentena = parseInt(userCDU.centena) || 0;
        const userDezena = parseInt(userCDU.dezena) || 0;
        const userUnidade = parseInt(userCDU.unidade) || 0;
        const userAnswer = userCentena * 100 + userDezena * 10 + userUnidade;
        
        const correct = userAnswer === op.correctAnswer;
        
        if (correct && !this.completedActivities[activityIndex]) {
          this.completedActivities[activityIndex] = true;
          this.correctAnswers++;
          this.checkAllActivitiesCompleted();
        } else if (!correct && this.completedActivities[activityIndex]) {
          this.completedActivities[activityIndex] = false;
          this.correctAnswers--;
        } else if (!correct && !this.completedActivities[activityIndex]) {
          // Erro ao tentar completar - perde uma vida
          this.wrongAnswers++;
          this.gameService.loseHeart();
          this.checkForBonusChallenge();
        }
      }
    }
  }

  getCDUValue(activityIndex: number, position: 'centena' | 'dezena' | 'unidade'): string {
    const key = `${activityIndex}-cdu`;
    return this.cduAnswers[key]?.[position] || '';
  }

  setCDUValue(activityIndex: number, position: 'centena' | 'dezena' | 'unidade', value: string): void {
    const key = `${activityIndex}-cdu`;
    if (!this.cduAnswers[key]) {
      this.cduAnswers[key] = { centena: '', dezena: '', unidade: '' };
    }
    this.cduAnswers[key][position] = value;
    // O total será calculado automaticamente pelo getCDUTotalValue
    this.checkInputActivity(activityIndex);
  }

  getCDUTotalValue(activityIndex: number): string {
    const key = `${activityIndex}-cdu`;
    const userCDU = this.cduAnswers[key];
    if (!userCDU) return '';
    
    const centena = userCDU.centena || '0';
    const dezena = userCDU.dezena || '0';
    const unidade = userCDU.unidade || '0';
    
    const total = parseInt(centena) * 100 + parseInt(dezena) * 10 + parseInt(unidade);
    return total > 0 ? String(total) : '';
  }

  setCDUTotalValue(activityIndex: number, value: string): void {
    const key = `${activityIndex}-cdu`;
    if (!this.cduAnswers[key]) {
      this.cduAnswers[key] = { centena: '', dezena: '', unidade: '' };
    }
    
    // Se o valor estiver vazio, limpar todos os campos
    if (!value || value.trim() === '') {
      this.cduAnswers[key].centena = '';
      this.cduAnswers[key].dezena = '';
      this.cduAnswers[key].unidade = '';
      this.checkInputActivity(activityIndex);
      return;
    }
    
    // Converter o valor total em C D U
    const num = parseInt(value) || 0;
    const centena = Math.floor(num / 100);
    const dezena = Math.floor((num % 100) / 10);
    const unidade = num % 10;
    
    this.cduAnswers[key].centena = centena > 0 ? String(centena) : '';
    this.cduAnswers[key].dezena = dezena > 0 ? String(dezena) : '';
    this.cduAnswers[key].unidade = String(unidade);
    
    this.checkInputActivity(activityIndex);
  }

  isCDUCorrect(activityIndex: number): boolean | null {
    if (!this.mission) return null;
    const activity = this.mission.activities[activityIndex];
    if (activity.type !== 'cdu-operation' || !activity.cduOperation) return null;
    
    const key = `${activityIndex}-cdu`;
    const userCDU = this.cduAnswers[key];
    if (!userCDU || !userCDU.centena || !userCDU.dezena || !userCDU.unidade) return null;
    
    const userCentena = parseInt(userCDU.centena) || 0;
    const userDezena = parseInt(userCDU.dezena) || 0;
    const userUnidade = parseInt(userCDU.unidade) || 0;
    const userAnswer = userCentena * 100 + userDezena * 10 + userUnidade;
    
    return userAnswer === activity.cduOperation.correctAnswer;
  }

  isInputFieldCorrect(activityIndex: number, fieldIndex: number): boolean | null {
    if (!this.mission) return null;
    const activity = this.mission.activities[activityIndex];
    if ((activity.type !== 'fill-blank' && activity.type !== 'math-input') || !activity.inputFields) return null;
    
    const field = activity.inputFields[fieldIndex];
    if (!field) return null;
    
    const key = this.getInputKey(activityIndex, fieldIndex);
    const userAnswer = this.inputAnswers[key] || '';
    if (!userAnswer) return null;
    
    const correctValue = String(field.correctValue).trim().toLowerCase();
    const userValue = userAnswer.trim().toLowerCase();
    
    return userValue === correctValue;
  }

  getCDUDisplay(number: number, position: number): string {
    const str = String(number).padStart(3, '0');
    return str[position] || '0';
  }

  // Métodos para Visual Multiplication
  checkVisualMultiplication(activityIndex: number): void {
    if (!this.mission) return;
    
    const player = this.gameService.getPlayer();
    if (player.hearts === 0 && !this.bonusChallengeCompleted) {
      this.checkForBonusChallenge();
      return;
    }
    
    if (this.showBonusChallenge && !this.bonusChallengeCompleted) {
      return;
    }
    
    const activity = this.mission.activities[activityIndex];
    if (activity.type !== 'visual-multiplication' || !activity.visualMultiplication) return;
    
    const vm = activity.visualMultiplication;
    
    // Verificar se todos os campos estão preenchidos
    let allFilled = true;
    vm.inputFields.forEach((field, fieldIndex) => {
      const key = this.getInputKey(activityIndex, fieldIndex);
      const userAnswer = this.inputAnswers[key] || '';
      if (!userAnswer.trim()) {
        allFilled = false;
      }
    });
    
    if (!allFilled) {
      return; // Ainda não preencheu tudo
    }
    
    // Construir a resposta do usuário
    // Se tiver 3 campos: num1 x num2 = result
    // Se tiver 2 campos: assume que o primeiro número já está na questão (ex: "2 x" ou "3 x")
    let userAnswer1 = '';
    let userAnswer2 = '';
    
    if (vm.inputFields.length === 3) {
      const num1 = this.inputAnswers[this.getInputKey(activityIndex, 0)] || '';
      const num2 = this.inputAnswers[this.getInputKey(activityIndex, 1)] || '';
      const result = this.inputAnswers[this.getInputKey(activityIndex, 2)] || '';
      userAnswer1 = `${num1} x ${num2} = ${result}`;
      userAnswer2 = `${num2} x ${num1} = ${result}`;
    } else if (vm.inputFields.length === 2) {
      // Extrair o primeiro número da questão (ex: "2 x" ou "3 x")
      const questionMatch = activity.question.match(/(\d+)\s*x/i);
      const firstNum = questionMatch ? questionMatch[1] : '';
      const num2 = this.inputAnswers[this.getInputKey(activityIndex, 0)] || '';
      const result = this.inputAnswers[this.getInputKey(activityIndex, 1)] || '';
      userAnswer1 = `${firstNum} x ${num2} = ${result}`;
      userAnswer2 = `${num2} x ${firstNum} = ${result}`;
    }
    
    // Verificar se a resposta está nas respostas corretas
    const isCorrect = vm.correctAnswers.some(correct => {
      const normalizedCorrect = correct.trim().toLowerCase();
      return normalizedCorrect === userAnswer1.trim().toLowerCase() || 
             normalizedCorrect === userAnswer2.trim().toLowerCase();
    });
    
    if (isCorrect && !this.completedActivities[activityIndex]) {
      this.completedActivities[activityIndex] = true;
      this.correctAnswers++;
      this.checkAllActivitiesCompleted();
    } else if (!isCorrect && this.completedActivities[activityIndex]) {
      this.completedActivities[activityIndex] = false;
      this.correctAnswers--;
    } else if (!isCorrect && !this.completedActivities[activityIndex]) {
      // Erro ao tentar completar - perde uma vida
      this.wrongAnswers++;
      this.gameService.loseHeart();
      this.checkForBonusChallenge();
    }
  }

  getVisualMultiplicationValue(activityIndex: number, fieldIndex: number): string {
    return this.getInputValue(activityIndex, fieldIndex);
  }

  setVisualMultiplicationValue(activityIndex: number, fieldIndex: number, value: string): void {
    this.setInputValue(activityIndex, fieldIndex, value);
    // Verificar após atualizar
    setTimeout(() => {
      this.checkVisualMultiplication(activityIndex);
    }, 100);
  }

  isVisualMultiplicationCorrect(activityIndex: number): boolean | null {
    if (!this.mission) return null;
    const activity = this.mission.activities[activityIndex];
    if (activity.type !== 'visual-multiplication' || !activity.visualMultiplication) return null;
    
    const vm = activity.visualMultiplication;
    
    // Verificar se todos os campos estão preenchidos
    let allFilled = true;
    vm.inputFields.forEach((field, fieldIndex) => {
      const key = this.getInputKey(activityIndex, fieldIndex);
      const userAnswer = this.inputAnswers[key] || '';
      if (!userAnswer.trim()) {
        allFilled = false;
      }
    });
    
    if (!allFilled) return null;
    
    // Construir a resposta do usuário
    let userAnswer1 = '';
    let userAnswer2 = '';
    
    if (vm.inputFields.length === 3) {
      const num1 = this.inputAnswers[this.getInputKey(activityIndex, 0)] || '';
      const num2 = this.inputAnswers[this.getInputKey(activityIndex, 1)] || '';
      const result = this.inputAnswers[this.getInputKey(activityIndex, 2)] || '';
      userAnswer1 = `${num1} x ${num2} = ${result}`;
      userAnswer2 = `${num2} x ${num1} = ${result}`;
    } else if (vm.inputFields.length === 2) {
      // Extrair o primeiro número da questão
      const questionMatch = activity.question.match(/(\d+)\s*x/i);
      const firstNum = questionMatch ? questionMatch[1] : '';
      const num2 = this.inputAnswers[this.getInputKey(activityIndex, 0)] || '';
      const result = this.inputAnswers[this.getInputKey(activityIndex, 1)] || '';
      userAnswer1 = `${firstNum} x ${num2} = ${result}`;
      userAnswer2 = `${num2} x ${firstNum} = ${result}`;
    }
    
    const isCorrect = vm.correctAnswers.some(correct => {
      const normalizedCorrect = correct.trim().toLowerCase();
      return normalizedCorrect === userAnswer1.trim().toLowerCase() || 
             normalizedCorrect === userAnswer2.trim().toLowerCase();
    });
    
    return isCorrect;
  }

  getArray(length: number): number[] {
    return Array(length).fill(0).map((_, i) => i);
  }

  // Método para filtrar apenas números
  filterNumbers(value: string): string {
    return value.replace(/[^0-9]/g, '');
  }

  // Método para calcular raiz quadrada (para usar no template)
  sqrt(value: number): number {
    return Math.sqrt(value);
  }

  // Métodos para C D U no Desafio 1 (math-input especial)
  getCDUFromTotal(activityIndex: number, position: 'centena' | 'dezena' | 'unidade'): string {
    if (activityIndex !== 0) return '';
    
    const totalValue = this.getInputValue(activityIndex, 0);
    if (!totalValue) return '';
    
    const num = parseInt(totalValue) || 0;
    if (num === 0) return '';
    
    if (position === 'centena') {
      return Math.floor(num / 100) > 0 ? String(Math.floor(num / 100)) : '';
    } else if (position === 'dezena') {
      return Math.floor((num % 100) / 10) > 0 ? String(Math.floor((num % 100) / 10)) : '';
    } else {
      return String(num % 10);
    }
  }

  setCDUFromTotal(activityIndex: number, position: 'centena' | 'dezena' | 'unidade', value: string): void {
    if (activityIndex !== 0) return;
    
    const key = `${activityIndex}-cdu-total`;
    if (!this.cduAnswers[key]) {
      this.cduAnswers[key] = { centena: '', dezena: '', unidade: '' };
    }
    
    this.cduAnswers[key][position] = value;
    
    // Recalcular o total a partir dos valores C D U
    const centena = parseInt(this.cduAnswers[key].centena || '0') || 0;
    const dezena = parseInt(this.cduAnswers[key].dezena || '0') || 0;
    const unidade = parseInt(this.cduAnswers[key].unidade || '0') || 0;
    const total = centena * 100 + dezena * 10 + unidade;
    
    // Atualizar o campo de total
    if (total > 0) {
      this.setInputValue(activityIndex, 0, String(total));
    } else {
      this.setInputValue(activityIndex, 0, '');
    }
  }
}

<div class="page" *ngIf="mission">
  <div class="container">
    <div class="game-layout">
      <div class="avatar-side">
        <app-avatar></app-avatar>
      </div>
      <div class="mission-side">
        <app-hud></app-hud>
        <div class="mission-header">
          <button class="btn-back" (click)="goBack()">← Voltar</button>
          <div class="title-with-info">
            <h1 class="mission-title">{{ mission.title }}</h1>
            <button *ngIf="mission.showImageIcon || (!mission.textContent && mission.image)" class="info-icon-btn" (click)="toggleOriginalImage()" title="Ver imagem original">
              ❓
            </button>
          </div>
        </div>

    <div class="mission-content">
      <!-- Seção: Conteúdo da Missão -->
      <section class="mission-section">
        <div class="image-container">
          <!-- Se tem texto ou imagens, mostrar -->
          <div *ngIf="(mission.textContent || mission.textImages) && !showOriginalImage" class="text-content-box">
            <h3 class="text-content-title" *ngIf="mission.textTitle">{{ mission.textTitle }}</h3>
            <!-- Imagens lado a lado se existirem -->
            <div *ngIf="mission.textImages && mission.textImages.length > 0" class="text-images-container">
              <div 
                *ngFor="let img of mission.textImages; let i = index" 
                class="text-image-wrapper">
                <h4 class="text-image-title" *ngIf="mission.textImageTitles && mission.textImageTitles[i]">
                  {{ mission.textImageTitles[i] }}
                </h4>
                <img 
                  [src]="img" 
                  [alt]="mission.textImageTitles && mission.textImageTitles[i] ? mission.textImageTitles[i] : (mission.textTitle || mission.title)"
                  class="text-image">
              </div>
            </div>
            <div class="text-content-body" *ngIf="mission.textContent">
              <p *ngFor="let paragraph of getTextParagraphs()" class="text-paragraph">
                {{ paragraph }}
              </p>
            </div>
          </div>
          
          <!-- Mostrar imagem original quando clicar no ícone ? (apenas se tem textImages ou textContent) -->
          <div *ngIf="showOriginalImage && (mission.textImages || mission.textContent)">
            <div class="original-image-container">
              <button class="close-image-btn" (click)="toggleOriginalImage()" title="Voltar">
                ✕ Fechar imagem
              </button>
            </div>
            <img 
              [src]="mission.image" 
              [alt]="mission.title" 
              class="mission-image">
          </div>
          
          <!-- Mostrar imagem quando não tem texto nem textImages (sempre visível) -->
          <div *ngIf="!mission.textContent && !mission.textImages">
            <img 
              [src]="mission.image" 
              [alt]="mission.title" 
              class="mission-image">
          </div>
        </div>
      </section>

      <!-- Seção: Explicação -->
      <section class="mission-section">
        <h2>📖 Vamos Aprender!</h2>
        <div class="explanation-text">
          <p *ngFor="let paragraph of mission.explanation.split('\n')">{{ paragraph }}</p>
        </div>
      </section>

      <!-- Seção: Atividade Interativa -->
      <section class="mission-section">
        <h2>🎮 Hora de Praticar!</h2>
        <div class="activity-container">
          <div class="activity-item" *ngFor="let activity of mission.activities; let activityIndex = index">
            <!-- Título do Desafio -->
            <div class="activity-title" *ngIf="activity.title">{{ activity.title }}</div>
            
            <!-- Instrução -->
            <div class="activity-instruction" *ngIf="activity.instruction">{{ activity.instruction }}</div>
            
            <!-- Descrição da Imagem -->
            <div class="activity-image-description" *ngIf="activity.imageDescription">
              <div class="image-description-label">📷 Imagem sugerida:</div>
              <div class="image-description-text">{{ activity.imageDescription }}</div>
            </div>
            
            <div class="activity-question">{{ activity.question }}</div>
            
            <!-- Recompensa (mostrar quando completar) -->
            <div class="activity-reward" *ngIf="activity.reward && completedActivities[activityIndex]">
              {{ activity.reward }}
            </div>
            
            <!-- Múltipla Escolha -->
            <div class="options-container" *ngIf="activity.type === 'multiple-choice' && activity.options" [class.options-with-images]="hasImagesInOptions(activity)">
              <button 
                class="option-btn"
                [class.has-image]="option.image"
                *ngFor="let option of (shuffledOptions[activityIndex] || activity.options); let optionIndex = index"
                (click)="checkAnswer(activityIndex, optionIndex, option.correct, option)">
                <img *ngIf="option.image" [src]="option.image" [alt]="option.text" class="option-image">
                <span class="option-text">{{ option.text }}</span>
              </button>
            </div>

            <!-- Verdadeiro ou Falso -->
            <div class="options-container" *ngIf="activity.type === 'true-false'">
              <button 
                class="option-btn"
                (click)="checkTrueFalse(activityIndex, true, activity.correct === true)">
                Verdadeiro ✅
              </button>
              <button 
                class="option-btn"
                (click)="checkTrueFalse(activityIndex, false, activity.correct === false)">
                Falso ❌
              </button>
            </div>

            <!-- Atividade de Match (Ligar Colunas) -->
            <div class="match-container" *ngIf="activity.type === 'match' && activity.matchPairs">
              <div class="match-instruction">Clique primeiro na imagem e depois no texto correspondente:</div>
              <div class="match-columns">
                <!-- Coluna de Imagens -->
                <div class="match-column match-images-column">
                  <div 
                    class="match-image-item"
                    *ngFor="let pair of shuffledMatchPairs[activityIndex]?.images || activity.matchPairs; let i = index"
                    [class.selected]="selectedImageForMatch[activityIndex] === pair.matchId"
                    [class.matched]="selectedMatches[activityIndex + '-' + pair.matchId] !== undefined"
                    (click)="selectImageForMatch(activityIndex, pair.matchId)">
                    <div class="match-image-box">
                      <img [src]="pair.image" [alt]="'Imagem ' + (i + 1)" class="match-image">
                    </div>
                    <div class="match-check" *ngIf="selectedMatches[activityIndex + '-' + pair.matchId] !== undefined">✅</div>
                  </div>
                </div>
                
                <!-- Coluna de Textos -->
                <div class="match-column match-texts-column">
                  <button 
                    class="match-text-btn"
                    *ngFor="let pair of shuffledMatchPairs[activityIndex]?.texts || activity.matchPairs"
                    [class.selected]="selectedMatches[activityIndex + '-' + pair.matchId] === pair.description"
                    [class.correct]="selectedMatches[activityIndex + '-' + pair.matchId] === pair.description"
                    (click)="selectMatch(activityIndex, pair.matchId, pair.description)">
                    {{ pair.description }}
                  </button>
                </div>
              </div>
            </div>

            <!-- Fiscal do Meio Ambiente -->
            <div class="environment-container" *ngIf="activity.type === 'environment-check' && activity.environmentImages">
              <div class="environment-instruction">Veja cada imagem e diga se tem problema ambiental:</div>
              <div class="environment-images">
                <div class="environment-item" *ngFor="let envImg of activity.environmentImages; let i = index">
                  <img [src]="envImg.image" [alt]="'Imagem ' + (i + 1)" class="environment-image">
                  <div class="environment-options">
                    <button 
                      class="env-btn"
                      [class.correct]="isEnvironmentCorrect(i) === true"
                      [class.incorrect]="isEnvironmentCorrect(i) === false"
                      (click)="checkEnvironment(activityIndex, i, true)">
                        Tem problema
                    </button>
                    <button 
                      class="env-btn"
                      [class.correct]="isEnvironmentCorrect(i) === false"
                      [class.incorrect]="isEnvironmentCorrect(i) === true"
                      (click)="checkEnvironment(activityIndex, i, false)">
                        Não tem problema
                    </button>
                  </div>
                  <div class="problem-description" *ngIf="envImg.problemDescription && ((envImg.hasProblem && isEnvironmentCorrect(i) === true) || (!envImg.hasProblem && isEnvironmentCorrect(i) === false))">
                    {{ envImg.problemDescription }}
                  </div>
                </div>
              </div>
            </div>

            <!-- Campos de Input (Fill Blank / Math Input) -->
            <div class="input-container" *ngIf="(activity.type === 'fill-blank' || activity.type === 'math-input') && activity.inputFields">
              <!-- Layout especial para Desafio 1 (com total e C D U) -->
              <div *ngIf="activityIndex === 0 && activity.inputFields.length === 2" class="math-input-special">
                <div class="math-input-total-row">
                  <div class="math-input-total-label">{{ activity.inputFields[0].label }}</div>
                  <input 
                    type="text"
                    class="math-input-total"
                    [class.correct]="isInputFieldCorrect(activityIndex, 0) === true"
                    [class.incorrect]="isInputFieldCorrect(activityIndex, 0) === false"
                    [placeholder]="activity.inputFields[0].placeholder || ''"
                    [value]="getInputValue(activityIndex, 0)"
                    (input)="setInputValue(activityIndex, 0, filterNumbers($any($event.target).value))"
                    [disabled]="completedActivities[activityIndex]">
                </div>
                <div class="math-input-cdu-row">
                  <div class="math-input-cdu-group">
                    <label class="math-input-cdu-label">C</label>
                    <input 
                      type="text"
                      class="math-input-cdu"
                      [class.correct]="isInputFieldCorrect(activityIndex, 0) === true && getCDUFromTotal(activityIndex, 'centena')"
                      [class.incorrect]="isInputFieldCorrect(activityIndex, 0) === false && getCDUFromTotal(activityIndex, 'centena')"
                      maxlength="1"
                      [value]="getCDUFromTotal(activityIndex, 'centena')"
                      (input)="setCDUFromTotal(activityIndex, 'centena', filterNumbers($any($event.target).value))"
                      [disabled]="completedActivities[activityIndex]"
                      placeholder="0">
                  </div>
                  <div class="math-input-cdu-group">
                    <label class="math-input-cdu-label">D</label>
                    <input 
                      type="text"
                      class="math-input-cdu"
                      [class.correct]="isInputFieldCorrect(activityIndex, 0) === true && getCDUFromTotal(activityIndex, 'dezena')"
                      [class.incorrect]="isInputFieldCorrect(activityIndex, 0) === false && getCDUFromTotal(activityIndex, 'dezena')"
                      maxlength="1"
                      [value]="getCDUFromTotal(activityIndex, 'dezena')"
                      (input)="setCDUFromTotal(activityIndex, 'dezena', filterNumbers($any($event.target).value))"
                      [disabled]="completedActivities[activityIndex]"
                      placeholder="0">
                  </div>
                  <div class="math-input-cdu-group">
                    <label class="math-input-cdu-label">U</label>
                    <input 
                      type="text"
                      class="math-input-cdu"
                      [class.correct]="isInputFieldCorrect(activityIndex, 0) === true && getCDUFromTotal(activityIndex, 'unidade')"
                      [class.incorrect]="isInputFieldCorrect(activityIndex, 0) === false && getCDUFromTotal(activityIndex, 'unidade')"
                      maxlength="1"
                      [value]="getCDUFromTotal(activityIndex, 'unidade')"
                      (input)="setCDUFromTotal(activityIndex, 'unidade', filterNumbers($any($event.target).value))"
                      [disabled]="completedActivities[activityIndex]"
                      placeholder="0">
                  </div>
                </div>
                <div class="math-input-conta-row">
                  <label class="input-label">{{ activity.inputFields[1].label }}</label>
                  <input 
                    type="text"
                    class="math-input"
                    [class.correct]="isInputFieldCorrect(activityIndex, 1) === true"
                    [class.incorrect]="isInputFieldCorrect(activityIndex, 1) === false"
                    [placeholder]="activity.inputFields[1].placeholder || ''"
                    [value]="getInputValue(activityIndex, 1)"
                    (input)="setInputValue(activityIndex, 1, $any($event.target).value)"
                    [disabled]="completedActivities[activityIndex]">
                  <span class="input-check" *ngIf="isInputFieldCorrect(activityIndex, 1) === true">✅</span>
                </div>
              </div>
              <!-- Layout padrão para outros math-input -->
              <div *ngIf="!(activityIndex === 0 && activity.inputFields.length === 2)" class="input-fields">
                <div class="input-field-group" *ngFor="let field of activity.inputFields; let fieldIndex = index">
                  <label class="input-label">{{ field.label }}</label>
                  <input 
                    type="text"
                    class="math-input"
                    [class.correct]="isInputFieldCorrect(activityIndex, fieldIndex) === true"
                    [class.incorrect]="isInputFieldCorrect(activityIndex, fieldIndex) === false"
                    [placeholder]="field.placeholder || ''"
                    [value]="getInputValue(activityIndex, fieldIndex)"
                    (input)="setInputValue(activityIndex, fieldIndex, $any($event.target).value)"
                    [disabled]="completedActivities[activityIndex]">
                  <span class="input-check" *ngIf="isInputFieldCorrect(activityIndex, fieldIndex) === true">✅</span>
                </div>
              </div>
            </div>

            <!-- Operação C D U (Centena, Dezena, Unidade) -->
            <div class="cdu-container" *ngIf="activity.type === 'cdu-operation' && activity.cduOperation">
              <div class="cdu-instruction">Monte a conta usando Centena, Dezena e Unidade:</div>
              <div class="cdu-operation">
                <div class="cdu-numbers">
                  <div class="cdu-number-row">
                    <div class="cdu-label">C D U</div>
                    <div class="cdu-digits">
                      <input type="text" class="cdu-input" [value]="getCDUDisplay(activity.cduOperation.number1, 0)" readonly>
                      <input type="text" class="cdu-input" [value]="getCDUDisplay(activity.cduOperation.number1, 1)" readonly>
                      <input type="text" class="cdu-input" [value]="getCDUDisplay(activity.cduOperation.number1, 2)" readonly>
                    </div>
                  </div>
                  <div class="cdu-operator">{{ activity.cduOperation.operation === 'addition' ? '+' : activity.cduOperation.operation === 'subtraction' ? '-' : 'x' }}</div>
                  <div class="cdu-number-row">
                    <div class="cdu-label">C D U</div>
                    <div class="cdu-digits">
                      <input type="text" class="cdu-input" [value]="getCDUDisplay(activity.cduOperation.number2, 0)" readonly>
                      <input type="text" class="cdu-input" [value]="getCDUDisplay(activity.cduOperation.number2, 1)" readonly>
                      <input type="text" class="cdu-input" [value]="getCDUDisplay(activity.cduOperation.number2, 2)" readonly>
                    </div>
                  </div>
                  <div class="cdu-line">─────</div>
                  <div class="cdu-number-row">
                    <div class="cdu-label">C D U</div>
                    <div class="cdu-digits">
                      <input 
                        type="text" 
                        class="cdu-input"
                        [class.correct]="isCDUCorrect(activityIndex) === true"
                        [class.incorrect]="isCDUCorrect(activityIndex) === false && getCDUValue(activityIndex, 'centena')"
                        maxlength="1"
                        pattern="[0-9]"
                        [value]="getCDUValue(activityIndex, 'centena')"
                        (input)="setCDUValue(activityIndex, 'centena', filterNumbers($any($event.target).value))"
                        [disabled]="completedActivities[activityIndex]"
                        placeholder="0">
                      <input 
                        type="text" 
                        class="cdu-input"
                        [class.correct]="isCDUCorrect(activityIndex) === true"
                        [class.incorrect]="isCDUCorrect(activityIndex) === false && getCDUValue(activityIndex, 'dezena')"
                        maxlength="1"
                        pattern="[0-9]"
                        [value]="getCDUValue(activityIndex, 'dezena')"
                        (input)="setCDUValue(activityIndex, 'dezena', filterNumbers($any($event.target).value))"
                        [disabled]="completedActivities[activityIndex]"
                        placeholder="0">
                      <input 
                        type="text" 
                        class="cdu-input"
                        [class.correct]="isCDUCorrect(activityIndex) === true"
                        [class.incorrect]="isCDUCorrect(activityIndex) === false && getCDUValue(activityIndex, 'unidade')"
                        maxlength="1"
                        pattern="[0-9]"
                        [value]="getCDUValue(activityIndex, 'unidade')"
                        (input)="setCDUValue(activityIndex, 'unidade', filterNumbers($any($event.target).value))"
                        [disabled]="completedActivities[activityIndex]"
                        placeholder="0">
                    </div>
                  </div>
                  <div class="cdu-check" *ngIf="isCDUCorrect(activityIndex) === true">✅ Correto!</div>
                </div>
              </div>
            </div>

            <!-- Visual Multiplication (Emojis agrupados ou Grade de quadrados) -->
            <div class="visual-multiplication-container" *ngIf="activity.type === 'visual-multiplication' && activity.visualMultiplication">
              <div class="visual-content">
                <!-- Grupos de Emojis -->
                <div class="emoji-groups" *ngIf="activity.visualMultiplication.emojiGroups">
                  <div class="emoji-group" *ngFor="let group of activity.visualMultiplication.emojiGroups">
                    <div class="emoji-group-label">Grupo de {{ group.count }} {{ group.emoji }}</div>
                    <div class="emoji-display">
                      <span *ngFor="let i of getArray(group.count)" class="emoji-item">{{ group.emoji }}</span>
                    </div>
                  </div>
                </div>

                <!-- Grade de Quadrados -->
                <div class="grid-container" *ngIf="activity.visualMultiplication.gridCells">
                  <div class="grid-label">Observe os quadrados na malha:</div>
                  <div class="math-grid" [style.grid-template-columns]="'repeat(' + (activity.visualMultiplication.gridCols || sqrt(activity.visualMultiplication.gridCells)) + ', 1fr)'">
                    <div class="grid-cell" *ngFor="let i of getArray(activity.visualMultiplication.gridCells)">■</div>
                  </div>
                </div>

                <!-- Campos de Input para completar a multiplicação -->
                <div class="visual-input-fields">
                  <div class="visual-input-group" *ngFor="let field of activity.visualMultiplication.inputFields; let fieldIndex = index">
                    <label class="visual-input-label">{{ field.label }}</label>
                    <input 
                      type="text"
                      class="visual-math-input"
                      [class.correct]="isVisualMultiplicationCorrect(activityIndex) === true && getVisualMultiplicationValue(activityIndex, fieldIndex)"
                      [class.incorrect]="isVisualMultiplicationCorrect(activityIndex) === false && getVisualMultiplicationValue(activityIndex, fieldIndex)"
                      [placeholder]="field.placeholder || ''"
                      [value]="getVisualMultiplicationValue(activityIndex, fieldIndex)"
                      (input)="setVisualMultiplicationValue(activityIndex, fieldIndex, $any($event.target).value)"
                      [disabled]="completedActivities[activityIndex]">
                  </div>
                  <div class="visual-check" *ngIf="isVisualMultiplicationCorrect(activityIndex) === true">✅ Correto!</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <!-- Desafio Bônus (quando perde todos os corações) -->
      <!-- Animação de coração recuperado -->
      <div class="heart-recovery-animation" *ngIf="showHeartAnimation">
        <div class="heart-card">
          <div class="heart-icon">❤️</div>
          <div class="heart-message">Vida recuperada!</div>
        </div>
      </div>

      <!-- Card de Perda de Moedas -->
      <div class="coins-loss-animation" *ngIf="showCoinsLossCard">
        <div class="coins-loss-card">
          <div class="coins-loss-icon">💸</div>
          <div class="coins-loss-title">Ops! Resposta Incorreta</div>
          <div class="coins-loss-amount">
            <span class="coins-icon">💎</span>
            <span class="coins-number">-{{ coinsLost }}</span>
          </div>
          <div class="coins-loss-hearts">
            <div class="hearts-label">❤️ Vidas</div>
            <div class="hearts-display">
              <span *ngFor="let hasHeart of getCurrentHeartsArray()" class="heart-display" [class.full]="hasHeart" [class.empty]="!hasHeart">
                {{ hasHeart ? '❤️' : '🤍' }}
              </span>
            </div>
          </div>
          <div class="coins-loss-message">Tente novamente! 😊</div>
          <div class="coins-flying coin-1">
            <span class="coin">💎</span>
          </div>
          <div class="coins-flying coin-2">
            <span class="coin">💎</span>
          </div>
          <div class="coins-flying coin-3">
            <span class="coin">💎</span>
          </div>
          <div class="coins-flying coin-4">
            <span class="coin">💎</span>
          </div>
          <div class="coins-flying coin-5">
            <span class="coin">💎</span>
          </div>
        </div>
      </div>

      <!-- Popup Modal do Desafio Bônus -->
      <div class="bonus-popup-overlay" *ngIf="showBonusChallenge && !bonusChallengeCompleted && mission" (click)="closeBonusPopup()">
        <div class="bonus-popup" (click)="$event.stopPropagation()">
          <div class="bonus-popup-header">
            <h2>💪 Desafio Bônus!</h2>
            <button class="close-popup-btn" (click)="closeBonusPopup()" *ngIf="false">✕</button>
          </div>
          <div class="bonus-popup-content">
            <p class="bonus-warning">Você perdeu todos os corações! 😢</p>
            <p class="bonus-message">Mas não desista! Responda esta pergunta para recuperar um coração! ❤️</p>
            <div class="bonus-question" *ngIf="mission.bonusQuestion">
              <p><strong>{{ mission.bonusQuestion }}</strong></p>
              <div class="options-container">
                <button 
                  class="option-btn" 
                  [class.selected]="bonusAnswerSelected === 'Sim'"
                  (click)="selectBonusAnswer('Sim')">
                  Sim
                </button>
                <button 
                  class="option-btn"
                  [class.selected]="bonusAnswerSelected === 'Não'"
                  (click)="selectBonusAnswer('Não')">
                  Não
                </button>
              </div>
              <button 
                class="btn-primary" 
                [disabled]="!bonusAnswerSelected"
                (click)="completeBonusChallenge()"
                style="margin-top: 15px;">
                Responder
              </button>
            </div>
            <div class="bonus-question" *ngIf="!mission.bonusQuestion">
              <p><strong>O que é multiplicação?</strong></p>
              <div class="options-container">
                <button 
                  class="option-btn"
                  [class.selected]="bonusAnswerSelected === 'Somar um número várias vezes'"
                  (click)="selectBonusAnswer('Somar um número várias vezes')">
                  Somar um número várias vezes
                </button>
                <button 
                  class="option-btn"
                  [class.selected]="bonusAnswerSelected === 'Subtrair números'"
                  (click)="selectBonusAnswer('Subtrair números')">
                  Subtrair números
                </button>
              </div>
              <button 
                class="btn-primary" 
                [disabled]="!bonusAnswerSelected"
                (click)="completeBonusChallenge()"
                style="margin-top: 15px;">
                Responder
              </button>
            </div>
          </div>
        </div>
      </div>

      <!-- Seção: Resumo Final -->
      <section class="mission-section success-section" id="success-section" style="display: none;">
        <h2>🎉 Parabéns, Anthony!</h2>
        <div class="success-message">
          <p>{{ mission.successMessage }}</p>
          <div class="reward-info" *ngIf="correctAnswers > 0">
            <p>✅ Você acertou {{ correctAnswers }} de {{ totalQuestions }} perguntas!</p>
          </div>
        </div>
        <button class="btn-primary" (click)="nextMission()" *ngIf="mission">
          ➡️ Próxima Missão
        </button>
      </section>
      </div>
      </div>
    </div>
  </div>
</div>

<!-- Card Final - Todas as Missões Completadas -->
<div class="final-card-overlay" *ngIf="showFinalCard" (click)="closeFinalCard()">
  <div class="final-card" (click)="$event.stopPropagation()">
    <div class="final-card-content">
      <div class="final-card-header">
        <h1 class="final-title">🎉 Parabéns, Anthony!</h1>
        <div class="final-subtitle">Você completou todas as missões!</div>
        <div class="final-message">Você é um verdadeiro mestre da matemática! 🏆</div>
      </div>
      
      <div class="final-guardian-container">
        <div class="final-avatar-wrapper">
          <app-avatar></app-avatar>
        </div>
        <div class="final-guardian-title">🔢 Mestre da Matemática 🔢</div>
      </div>
      
      <div class="final-stats">
        <div class="final-stat-item">
          <span class="final-stat-icon">⭐</span>
          <span class="final-stat-label">Nível {{ gameService.getPlayer().level }}</span>
        </div>
        <div class="final-stat-item">
          <span class="final-stat-icon">💎</span>
          <span class="final-stat-label">{{ gameService.getPlayer().coins }} Moedas</span>
        </div>
        <div class="final-stat-item">
          <span class="final-stat-icon">🎁</span>
          <span class="final-stat-label">{{ gameService.getPlayer().items.length }} Itens</span>
        </div>
      </div>
      
      <button class="final-card-btn" (click)="closeFinalCard()">
        🏠 Voltar para Missões
      </button>
    </div>
  </div>
</div>
